server '', :app, :web, :db, :primary => true

set :rails_env, "staging"
RUBY
    end

    create_file 'config/deploy/production.rb' do
<<-RUBY
puts '*** Deploying to: PRODUCTION environment ***'

server '', :app, :web, :db, :primary => true

set :rails_env, "production"
RUBY
    end

  end
end


